sys_script.script:
(function executeRule(current, previous /*null when async*/) {

    if (current.vehicle_status == "sent for servicing" && previous.vehicle_status != "sent for servicing") {
        gs.info("vehicle_status changed to sent for servicing");

        var servicingGR = new GlideRecord('x_1474716_lva_vehicle_servicing');
        servicingGR.addQuery('vehicle_tracker', current.sys_id);
        servicingGR.addQuery('ticket_status', 'Open');
        servicingGR.query();

        if (!servicingGR.next()) {
            gs.info("No existing open servicing ticket found. Creating new...");

            var newServicing = new GlideRecord('x_1474716_lva_vehicle_servicing');
            newServicing.initialize();
            newServicing.vehicle_tracker = current.sys_id;
            newServicing.vehicle = current.vehicle;
            newServicing.vehicle_status = "in service";
            newServicing.date_sent_for_servicing = new GlideDateTime();
            newServicing.ticket_status = "Open";
            newServicing.insert();

            gs.info("Created new servicing ticket for vehicle tracker: " + current.sys_id);
        } else {
            gs.info("Existing open ticket found. No new ticket created.");
        }
    }

})(current, previous);
