sys_script.script:
(function executeRule(current, previous /*null when async*/) {
    var trackerStatus = current.vehicle_status.toString(); // Ensure string comparison

    var vehicleGR = new GlideRecord('x_1474716_lva_vehicle_loaner_catalog');

    if (vehicleGR.get(current.vehicle)) {

		var newStatus = "";

        if (trackerStatus == "pending release" || 
            trackerStatus == "out on the field" || 
            trackerStatus == "returned for inspection" || 
            trackerStatus == "sent for servicing") {

            newStatus = "Unavailable";

        } else if (trackerStatus == "returned to warehouse") {
            newStatus = "Available";

        } else if (trackerStatus == "decommissioned") {
            newStatus = "Decommissioned";
        }

		if (newStatus !== "") {
			gs.info("Setting Loaner Vehicle status to: " + newStatus);
			vehicleGR.setValue("status", newStatus);
			vehicleGR.update();
		}
    }
})(current, previous);
